
  DECLARE 
   STATUS NUMBER; 
 BEGIN
   STATUS := DBMS_JAVA.START_IMPORT('testng.css', 0,30,0,3,326,0);
   IF STATUS !=0 THEN 
     IF STATUS > 0 THEN STATUS := -STATUS; END IF; 
     execute immediate 
     'declare an exception; pragma exception_init(an, '
     || status || '); ' || 'begin raise an; end;'; 
   END IF; 
 
  END;
