
  CREATE OR REPLACE FORCE VIEW "LONTANA"."CRM_V_ABONENTS" ("PHONE_NUMBER_FEDERAL", "ABONENT_ID", "SURNAME", "NAME", "PATRONYMIC", "BDATE", "PASSPORT_SER", "PASSPORT_NUM", "PASSPORT_DATE", "PASSPORT_GET", "CITIZENSHIP", "COUNTRY_ID", "REGION_ID", "REGION_NAME", "CITY_NAME", "STREET_NAME", "HOUSE", "KORPUS", "APARTMENT", "CONTACT_INFO", "CODE_WORD", "IS_VIP", "USER_CREATED", "DATE_CREATED", "USER_LAST_UPDATED", "DATE_LAST_UPDATED", "EMAIL", "DESCRIPTION") AS 
  SELECT CO.PHONE_NUMBER_FEDERAL, AB."ABONENT_ID",AB."SURNAME",AB."NAME",AB."PATRONYMIC",AB."BDATE",AB."PASSPORT_SER",AB."PASSPORT_NUM",AB."PASSPORT_DATE",AB."PASSPORT_GET",AB."CITIZENSHIP",AB."COUNTRY_ID",AB."REGION_ID",AB."REGION_NAME",AB."CITY_NAME",AB."STREET_NAME",AB."HOUSE",AB."KORPUS",AB."APARTMENT",AB."CONTACT_INFO",AB."CODE_WORD",AB."IS_VIP",AB."USER_CREATED",AB."DATE_CREATED",AB."USER_LAST_UPDATED",AB."DATE_LAST_UPDATED",AB."EMAIL",AB."DESCRIPTION" 
FROM ABONENTS AB, CONTRACTS CO
WHERE CO.ABONENT_ID=AB.ABONENT_ID