
  CREATE OR REPLACE FORCE VIEW "LONTANA"."CRM_V_BILLSFORCLIENT" ("LOGIN", "ACCOUNT_ID", "YEAR_MONTH", "PHONE_NUMBER", "BILL_SUM_NEW", "ABON_TP_NEW", "ABON_ADD_NEW", "DISCOUNT_NEW", "SINGLE_PAYMENTS_NEW", "DISCOUNT_CALL", "DISCOUNT_SMS_PLUS", "SINGLE_CHANGE_TARIFF", "SINGLE_VIEW_BLACK_LIST", "SINGLE_CORRECTION_ROUMING", "SINGLE_INTRA_WEB", "SINGLE_PENALTI", "SINGLE_TURN_ON_SERV", "SINGLE_OTHER", "CALLS_COUNTRY", "CALLS_SITY", "CALLS_LOCAL", "CALLS_SMS_MMS", "CALLS_GPRS", "CALLS_RUS_RPP", "ROUMING_NATIONAL", "ROUMING_INTERNATIONAL") AS 
