
  CREATE OR REPLACE FORCE VIEW "CORP_MOBILE"."V_BILL_FINANCE_FOR_GROUP_DET" ("LOGIN", "ACCOUNT_ID", "YEAR_MONTH", "PHONE_NUMBER", "BILL_SUM_NEW", "ABON_TP_NEW", "ABON_ADD_NEW", "DISCOUNT_NEW", "SINGLE_PAYMENTS_NEW", "DISCOUNT_CALL", "DISCOUNT_SMS_PLUS", "SINGLE_CHANGE_TARIFF", "SINGLE_VIEW_BLACK_LIST", "SINGLE_CORRECTION_ROUMING", "SINGLE_INTRA_WEB", "SINGLE_PENALTI", "SINGLE_TURN_ON_SERV", "SINGLE_OTHER", "CALLS_COUNTRY", "CALLS_SITY", "CALLS_LOCAL", "CALLS_SMS_MMS", "CALLS_GPRS", "CALLS_RUS_RPP", "ROUMING_NATIONAL", "ROUMING_INTERNATIONAL", "CONTRACT_ID", "CONTRACT_NUM", "CONTRACT_DATE", "FILIAL_ID", "OPERATOR_ID", "PHONE_NUMBER_FEDERAL", "PHONE_NUMBER_CITY", "PHONE_NUMBER_TYPE", "TARIFF_ID", "SIM_NUMBER", "CONTRACT_CHANGE_DATE", "SERVICE_ID", "DISCONNECT_LIMIT", "CONNECT_LIMIT", "CONFIRMED", "USER_CREATED", "DATE_CREATED", "USER_LAST_UPDATED", "DATE_LAST_UPDATED", "ABONENT_ID", "HAND_BLOCK", "HAND_BLOCK_DATE_END", "IS_CREDIT_CONTRACT", "CONTRACT_CANCEL_DATE", "DOP_STATUS", "BALANCE_NOTICE_HAND_BLOCK", "BALANCE_BLOCK_HAND_BLOCK", "DEALER_KOD", "GROUP_ID") AS 
  SELECT B."LOGIN",B."ACCOUNT_ID",B."YEAR_MONTH",B."PHONE_NUMBER",B."BILL_SUM_NEW",B."ABON_TP_NEW",B."ABON_ADD_NEW",B."DISCOUNT_NEW",B."SINGLE_PAYMENTS_NEW",B."DISCOUNT_CALL",B."DISCOUNT_SMS_PLUS",B."SINGLE_CHANGE_TARIFF",B."SINGLE_VIEW_BLACK_LIST",B."SINGLE_CORRECTION_ROUMING",B."SINGLE_INTRA_WEB",B."SINGLE_PENALTI",B."SINGLE_TURN_ON_SERV",B."SINGLE_OTHER",B."CALLS_COUNTRY",B."CALLS_SITY",B."CALLS_LOCAL",B."CALLS_SMS_MMS",B."CALLS_GPRS",B."CALLS_RUS_RPP",B."ROUMING_NATIONAL",B."ROUMING_INTERNATIONAL", C."CONTRACT_ID",C."CONTRACT_NUM",C."CONTRACT_DATE",C."FILIAL_ID",C."OPERATOR_ID",C."PHONE_NUMBER_FEDERAL",C."PHONE_NUMBER_CITY",C."PHONE_NUMBER_TYPE",C."TARIFF_ID",C."SIM_NUMBER",C."CONTRACT_CHANGE_DATE",C."SERVICE_ID",C."DISCONNECT_LIMIT",C."CONNECT_LIMIT",C."CONFIRMED",C."USER_CREATED",C."DATE_CREATED",C."USER_LAST_UPDATED",C."DATE_LAST_UPDATED",C."ABONENT_ID",C."HAND_BLOCK",C."HAND_BLOCK_DATE_END",C."IS_CREDIT_CONTRACT",C."CONTRACT_CANCEL_DATE",C."DOP_STATUS",C."BALANCE_NOTICE_HAND_BLOCK",C."BALANCE_BLOCK_HAND_BLOCK",C."DEALER_KOD",C."GROUP_ID"
FROM
  V_BILL_FINANCE_FOR_CLIENT_DET B, V_CONTRACTS C
WHERE B.PHONE_NUMBER = C.PHONE_NUMBER_FEDERAL
  AND C.CONTRACT_DATE < ADD_MONTHS(TO_DATE(b.YEAR_MONTH||'01', 'yyyymmdd'), 1)
  AND (C.CONTRACT_CANCEL_DATE IS NULL OR C.CONTRACT_CANCEL_DATE >= TO_DATE(b.YEAR_MONTH||'01', 'yyyymmdd'))
  AND C.GROUP_ID IS NOT NULL
