
  CREATE OR REPLACE FORCE VIEW "CORP_MOBILE"."V_BILL_ROUMING_FOR_CLIENT" ("ACCOUNT_ID", "YEAR_MONTH", "PHONE_NUMBER", "DATE_BEGIN", "DATE_END", "ROUMING_COUNTRY", "ROUMING_CALLS", "ROUMING_GPRS", "ROUMING_SMS", "COMPANY_TAX", "ROUMING_SUM") AS 
  SELECT MG."ACCOUNT_ID",MG."YEAR_MONTH",MG."PHONE_NUMBER",MG."DATE_BEGIN",MG."DATE_END",MG."ROUMING_COUNTRY",MG."ROUMING_CALLS",MG."ROUMING_GPRS",MG."ROUMING_SMS",MG."COMPANY_TAX",MG."ROUMING_SUM"
    FROM DB_LOADER_FULL_BILL_MG_ROUMING MG
  UNION ALL 
  SELECT MN."ACCOUNT_ID",MN."YEAR_MONTH",MN."PHONE_NUMBER",MN."DATE_BEGIN",MN."DATE_END",MN."ROUMING_COUNTRY",MN."ROUMING_CALLS",MN."ROUMING_GPRS",MN."ROUMING_SMS",MN."COMPANY_TAX",MN."ROUMING_SUM"
    FROM DB_LOADER_FULL_BILL_MN_ROUMING MN 