
  CREATE OR REPLACE FORCE VIEW "SIM_TRADE"."V_REPORT_SUMMARY_MINUTES" ("ACCOUNT_ID", "YEAR_MONTH", "PHONE_NUMBER", "BALANCE_VALUE", "OPERATOR_NAME", "TARIFF_NAME", "TARIFF_ID", "ESTIMATE_SUM", "ZEROCOST_OUTCOME_MINUTES", "ZEROCOST_OUTCOME_COUNT", "CALLS_COUNT", "CALLS_MINUTES", "CALLS_COST", "SMS_COUNT", "SMS_COST", "MMS_COUNT", "MMS_COST", "INTERNET_MB", "INTERNET_COST", "OTHER_SERVICES_COST") AS 
  SELECT
--#VERSION=1
  V_REPORT_PHONE_STAT."ACCOUNT_ID",V_REPORT_PHONE_STAT."YEAR_MONTH",V_REPORT_PHONE_STAT."PHONE_NUMBER",V_REPORT_PHONE_STAT."BALANCE_VALUE",V_REPORT_PHONE_STAT."OPERATOR_NAME",V_REPORT_PHONE_STAT."TARIFF_NAME",V_REPORT_PHONE_STAT."TARIFF_ID",V_REPORT_PHONE_STAT."ESTIMATE_SUM",V_REPORT_PHONE_STAT."ZEROCOST_OUTCOME_MINUTES",V_REPORT_PHONE_STAT."ZEROCOST_OUTCOME_COUNT",V_REPORT_PHONE_STAT."CALLS_COUNT",V_REPORT_PHONE_STAT."CALLS_MINUTES",V_REPORT_PHONE_STAT."CALLS_COST",V_REPORT_PHONE_STAT."SMS_COUNT",V_REPORT_PHONE_STAT."SMS_COST",V_REPORT_PHONE_STAT."MMS_COUNT",V_REPORT_PHONE_STAT."MMS_COST",V_REPORT_PHONE_STAT."INTERNET_MB",V_REPORT_PHONE_STAT."INTERNET_COST",V_REPORT_PHONE_STAT."OTHER_SERVICES_COST"
FROM
  V_REPORT_PHONE_STAT,
  TARIFFS
WHERE
  V_REPORT_PHONE_STAT.TARIFF_ID=TARIFFS.TARIFF_ID
  -- Количество бесплатных минут превышает лимит, заданный в справочнике.
  AND V_REPORT_PHONE_STAT.ZEROCOST_OUTCOME_MINUTES > TARIFFS.FREE_MONTH_MINUTES_CNT_FOR_RPT 