
  CREATE OR REPLACE FORCE VIEW "SIM_TRADE"."V_TMP_BILL_FULL_COPMARE" ("BILL_SUM_NO_DET", "ACCOUNT_ID", "YEAR_MONTH", "PHONE_NUMBER", "ABONKA", "CALLS", "SINGLE_PAYMENTS", "DISCOUNTS", "BILL_SUM", "COMPLETE_BILL", "ABON_MAIN", "ABON_ADD", "ABON_OTHER", "SINGLE_MAIN", "SINGLE_ADD", "SINGLE_PENALTI", "SINGLE_CHANGE_TARIFF", "SINGLE_TURN_ON_SERV", "SINGLE_CORRECTION_ROUMING", "SINGLE_INTRA_WEB", "SINGLE_VIEW_BLACK_LIST", "SINGLE_OTHER", "DISCOUNT_YEAR", "DISCOUNT_SMS_PLUS", "DISCOUNT_CALL", "DISCOUNT_COUNT_ON_PHONES", "DISCOUNT_OTHERS", "CALLS_COUNTRY", "CALLS_SITY", "CALLS_LOCAL", "CALLS_SMS_MMS", "CALLS_GPRS", "CALLS_RUS_RPP", "CALLS_ALL", "ROUMING_NATIONAL", "ROUMING_INTERNATIONAL", "DATE_CREATED", "USER_CREATED", "USER_LAST_UPDATED", "DATE_LAST_UPDATED", "DISCOUNT_SOVINTEL", "BAN") AS 
  select B.BILL_SUM BILL_SUM_NO_DET, FB."ACCOUNT_ID",FB."YEAR_MONTH",FB."PHONE_NUMBER",FB."ABONKA",FB."CALLS",FB."SINGLE_PAYMENTS",FB."DISCOUNTS",FB."BILL_SUM",FB."COMPLETE_BILL",FB."ABON_MAIN",FB."ABON_ADD",FB."ABON_OTHER",FB."SINGLE_MAIN",FB."SINGLE_ADD",FB."SINGLE_PENALTI",FB."SINGLE_CHANGE_TARIFF",FB."SINGLE_TURN_ON_SERV",FB."SINGLE_CORRECTION_ROUMING",FB."SINGLE_INTRA_WEB",FB."SINGLE_VIEW_BLACK_LIST",FB."SINGLE_OTHER",FB."DISCOUNT_YEAR",FB."DISCOUNT_SMS_PLUS",FB."DISCOUNT_CALL",FB."DISCOUNT_COUNT_ON_PHONES",FB."DISCOUNT_OTHERS",FB."CALLS_COUNTRY",FB."CALLS_SITY",FB."CALLS_LOCAL",FB."CALLS_SMS_MMS",FB."CALLS_GPRS",FB."CALLS_RUS_RPP",FB."CALLS_ALL",FB."ROUMING_NATIONAL",FB."ROUMING_INTERNATIONAL",FB."DATE_CREATED",FB."USER_CREATED",FB."USER_LAST_UPDATED",FB."DATE_LAST_UPDATED",FB."DISCOUNT_SOVINTEL",FB."BAN"
    from tmpdb_loader_full_finance_bill fb
    left join tmpdb_loader_bills b on fb.ACCOUNT_ID = b.ACCOUNT_ID and fb.YEAR_MONTH = b.YEAR_MONTH and fb.PHONE_NUMBER = b.PHONE_NUMBER
    where (FB.COMPLETE_BILL = 1) 
      AND (round(fb.BILL_SUM, 2) <> b.BILL_SUM
      or round(fb.SINGLE_PAYMENTS, 2) <> b.SINGLE_PAYMENTS
      or round(fb.ABON_MAIN , 2) <> b.SUBSCRIBER_PAYMENT_MAIN
      or round(fb.ABON_ADD, 2) <> b.SUBSCRIBER_PAYMENT_ADD
      or round(fb.DISCOUNTS , 2) <> b.DISCOUNT_VALUE
      OR ( FB.PHONE_NUMBER IS NULL AND B.PHONE_NUMBER IS NOT NULL)) 